{"$file\" ]; then\n        sed -i": "category:.*$/category: \"$new_cat\"/", "$file\"\n        if [ -n \"$new_sub\" ]; then\n            # Update or add subcategory\n            if grep -q": "subcategory:", "$file\"; then\n                sed -i": "subcategory:.*$/subcategory: \"$new_sub\"/", "$file\"\n            else\n                # Add after category line\n                sed -i": "category:/a subcategory: \"$new_sub\"", "$file": "i\n        fi\n    fi"}